# ---Basic(Workhorse)---
def add_binary(a: str, b: str) -> str:
    i, j = len(a)-1, len(b)-1

    carry = 0

    res = []

    while i >= 0 or j >= 0 or carry:
        current_sum = carry
        if i >= 0:
            current_sum += int(a[i])
            i -= 1
        if j >= 0:
            current_sum += int(b[j])
            j -= 1
        
        res.append(str(current_sum % 2))
        carry = current_sum // 2
    
    if carry:
        res = ["1"] + res
    
    return "".join(reversed(res))

# ---BigINT---
def add_binary(a, b):
    return bin(int(a, 2) + int(b, 2))[2:]

# ---Elegant---
def add_binary(a, b):
    from itertools import zip_longest

    carry = 0
    res = []

    for char_a, char_b in zip_longest(a[::-1], b[::-1], fillvalue='0'):
        sum_ = int(char_a) + int(char_b) + carry

        res.append(str(sum_ % 2))
        carry = sum_ // 2
    
    if carry:
        res.append('1')
    
    return "".join(res[::-1])

# ---CPU Simulation---
def add_binary(a: str, b: str):
    max_len = (max(len(a), len(b)))
    a = a.zfill(max_len)
    b = b.zfill(max_len)

    carry = 0
    res = []

    for i in range(max_len-1, -1, -1):
        bit_a = int(a[i])
        bit_b = int(b[i])

        current_sum_bit = bit_a ^ bit_b ^ carry
        carry_out = (bit_a & bit_b) | (carry & (bit_a ^ bit_b))

        res.append(str(current_sum_bit))
        carry = carry_out
    
    if carry:
        res.append('1')
    
    return "".join(res[::-1])


print(add_binary(a = "11", b = "1"), "\n\n")
print(add_binary(a = "1010", b = "1011"), "\n\n")
print(add_binary(a = "0", b = "1"), "\n\n")
print(add_binary(a = "0", b = "0"), "\n\n")
print(add_binary(a = "1", b = "0"), "\n\n")
print(add_binary(a = "",
                 b = ""))